customCSS:
/* HOST STYLES - Styles for elements outside shadow DOM */
.chat-toggle-btn {
  position: fixed;
  right: 24px;
  bottom: 24px;
  width: 56px;
  height: 56px;
  border-radius: 50%;
  border: none;
  cursor: pointer;
  z-index: 10000;
  box-shadow: 0 8px 24px rgba(0,0,0,0.25);
  color: #fff;
  font-size: 22px;
  transition: all 0.3s ease;
}

.chat-popup {
  position: fixed;
  right: 24px;
  bottom: 92px;
  width: 380px;
  height: 560px;
  max-width: 95vw;
  max-height: 85vh;
  background: #ffffff;
  border: 1px solid #E4E7EC;
  border-radius: 16px;
  box-shadow: 0 10px 25px rgba(0,0,0,0.25);
  overflow: hidden;
  z-index: 9999;
}

/* SHADOW DOM STYLES START */
* {
  font-size: 16px;
  line-height: 22.5px;
  font-weight: 400;
}
* p {
  margin-block-start: 0;
  margin-block-end: 0;
}

#bsc-chat-container {
    width: 350px;
    z-index: 1004;
    bottom: 20px;
    right: 20px;
    height: 85vh;
    border-radius: 15px;
    box-shadow: 0px 4px 6px 0px rgba(0, 0, 0, 0.25);
}
#bsc-chat-container * {
  font-family: "RobotoRegural", sans-serif;
}
#bsc-chat-container a {
  color: #007aff;
}
#bsc-chat-container a:hover, #bsc-chat-container a:active, #bsc-chat-container a:visited {
  color: #8779e7;
}
#bsc-chat-container.bsc-chat-container-minimized .bsc-header {
  display: none;
  outline: auto;
}
#bsc-chat-container .bsc-header {
  flex: 65px 0 0;
  background-color: #007aff;
  padding: 15px 20px;
  border-radius: 15px 15px 0 0;
}
#bsc-chat-container .bsc-header-details {
  align-items: center;
}
#bsc-chat-container .bsc-header-details-image {
  border-radius: 50%;
  background-image: url("https://webchat-themes.ng.botschool.ai/private-themes/botschool2.0/images/default_student.jpg");
  background-repeat: no-repeat, no-repeat;
  background-size: cover;
  background-position: 50% 50%;
  border: 2px solid #fff;
  width: 35px;
  height: 35px;
}
#bsc-chat-container .bsc-header-details-text {
  padding-left: 10px;
  padding-top: 2px;
}
#bsc-chat-container .bsc-header-details-text-title {
  transform: none;
  color: #fff;
  font-size: 16px;
  font-weight: 400;
}
#bsc-chat-container .bsc-header-action-minimize-icon {
  mask-image: url("https://webchat-themes.ng.botschool.ai/private-themes/botschool2.0/icons/minimize.svg");
  -webkit-mask-image: url("https://webchat-themes.ng.botschool.ai/private-themes/botschool2.0/icons/minimize.svg");
  background-color: #fff;
}
#bsc-chat-container .bsc-header-action-minimize-icon:hover {
  opacity: 1;
}
#bsc-chat-container .bsc-header-action-close-icon {
  mask-image: url("https://webchat-themes.ng.botschool.ai/private-themes/botschool2.0/icons/close.svg");
  -webkit-mask-image: url("https://webchat-themes.ng.botschool.ai/private-themes/botschool2.0/icons/close.svg");
  background-color: #fff;
}
#bsc-chat-container .bsc-header-action-close-icon:hover {
  opacity: 1;
}
#bsc-chat-container .bsc-message-list {
  background-color: #fff;
  overflow-y: auto;
  overflow-x: hidden;
}
#bsc-chat-container .bsc-message-list::-webkit-scrollbar {
  width: 8px;
}
#bsc-chat-container .bsc-message-list::-webkit-scrollbar-track {
  background: rgba(232, 234, 237, 0.3);
  border-radius: 10px;
  margin: 10px 0;
}
#bsc-chat-container .bsc-message-list::-webkit-scrollbar-thumb {
  background: linear-gradient(180deg, #D4A024 0%, #C8941D 100%);
  border-radius: 10px;
  border: 2px solid transparent;
  background-clip: padding-box;
}
#bsc-chat-container .bsc-message-list::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(180deg, #E5B135 0%, #D9A52E 100%);
  background-clip: padding-box;
}
#bsc-chat-container .bsc-message-list .bsc-message-list-container {
  padding: 15px 3px 10px 20px;
  overflow: scroll;
  overflow-x: hidden;
}
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-local .bsc-message {
  background-color: #007aff;
  color: white;
  border-radius: 15px 15px 0 15px;
}
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-remote {
  max-width: 303px;
}
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-remote .bsc-message {
  background-color: #edeef2;
  color: #666;
  border-radius: 15px 15px 15px 0;
}
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-local .bsc-message,
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-remote .bsc-message {
  padding: 10px;
  width: fit-content;
  box-shadow: 0px 4px 4px -3px rgba(0, 0, 0, 0.25);
}
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-local .bsc-message .dot-1, #bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-local .bsc-message .dot-2, #bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-local .bsc-message .dot-3,
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-remote .bsc-message .dot-1,
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-remote .bsc-message .dot-2,
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-remote .bsc-message .dot-3 {
  background-color: #007aff;
}
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-local .bsc-message-menuButtons-title,
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-remote .bsc-message-menuButtons-title {
  padding: 0 0 8px 0;
}
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-local .bsc-message-menuButtons-vertical button, #bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-local .bsc-message-menuButtons-horizontal button, #bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-local .bsc-message-menuButtons-grid button,
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-remote .bsc-message-menuButtons-vertical button,
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-remote .bsc-message-menuButtons-horizontal button,
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-remote .bsc-message-menuButtons-grid button {
  line-height: 21px;
  padding: 0.25rem 0.5rem;
  border-radius: 6px;
  color: white;
  background-color: #007aff;
  border: inherit;
  border-color: #007aff;
  transition: color 0.15s ease-in-out, background-color 0.15s ease-in-out, border-color 0.15s ease-in-out, box-shadow 0.15s ease-in-out;
}
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-local .bsc-message-menuButtons-vertical :hover, #bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-local .bsc-message-menuButtons-horizontal :hover, #bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-local .bsc-message-menuButtons-grid :hover,
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-remote .bsc-message-menuButtons-vertical :hover,
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-remote .bsc-message-menuButtons-horizontal :hover,
#bsc-chat-container .bsc-message-list .bsc-message-list-container .bsc-remote .bsc-message-menuButtons-grid :hover {
  background-color: #8779e7;
}
#bsc-chat-container .bsc-footer {
  padding: 0px;
  position: relative;
  background-color: #EDEEF2;
  border-radius: 0 0 15px 15px;
  flex: 55px 0 0;
}
#bsc-chat-container .bsc-footer #bsc-footer-zones .bsc-footer-zone-1 {
  flex: 40px 0 0;
  background-color: transparent;
  border: none;
  background: linear-gradient(90deg, #9059f7 3%, #de6af7 100%);
  border-radius: 50%;
  font-size: 22px;
  padding: 0;
  transition: transform 0.3s;
  margin-right: 10px;
}
#bsc-chat-container .bsc-footer #bsc-footer-zones .bsc-footer-zone-1:hover {
  transform: scale(1.1);
}
#bsc-chat-container .bsc-footer #bsc-footer-actions #bsc-send-button {
  border: none;
  background-color: #007aff;
  margin-right: 5px;
  margin-left: 10px;
}
#bsc-chat-container .bsc-footer #bsc-footer-actions #bsc-send-button:hover {
  background-color: #8779e7;
}

.bsc-chat-minimized {
    background-image: url("https://webchat-themes.ng.botschool.ai/private-themes/botschool2.0/images/default_student.jpg");
    background-repeat: no-repeat, no-repeat;
    background-size: cover;
    background-position: 50% 50%;
    box-shadow: 0px 5px 2px rgba(0, 0, 0, 0.25);
    bottom: 70px;
    right: 25px;
}

.bsc-chat-minimized .bsc-text-container{
    display: none !important;
}
/* SHADOW DOM STYLES END */
